// src/pages/PaymentSuccess.jsx
import { useNavigate, useLocation } from "react-router-dom";

export default function PaymentSuccess() {
  const navigate = useNavigate();
  const location = useLocation();

  // data order dari halaman Payment
  const { orderId, items, total } = location.state || {};

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-[#f5efe6] px-4">
      <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md text-center border border-[#e0d6c3]">
        {/* Pesan sukses */}
        <h1 className="text-2xl font-bold text-[#6F4E37] mb-4">Selamat ðŸŽ‰</h1>
        <p className="text-[#6F4E37] mb-6">Pesanan akan segera kami buatkan.</p>

        {/* Container Order ID */}
        <div className="bg-[#f5efe6] border border-[#d6c9b8] rounded-lg p-3 mb-6">
          <p className="text-sm text-gray-600">Order ID:</p>
          <p className="font-bold text-lg text-[#6F4E37]">{orderId}</p>
        </div>

        {/* Ringkasan pesanan */}
        <div className="text-left mb-6">
          <h2 className="font-semibold text-[#6F4E37] mb-2">Pesanan Anda:</h2>
          <ul className="space-y-1">
            {items?.map((item) => (
              <li key={item.id} className="flex justify-between text-sm">
                <span>
                  {item.quantity}x {item.name}
                </span>
                <span>Rp{(item.price * item.quantity).toLocaleString()}</span>
              </li>
            ))}
          </ul>
          <div className="border-t border-gray-300 mt-3 pt-2 flex justify-between font-bold text-[#6F4E37]">
            <span>Total:</span>
            <span>Rp{total?.toLocaleString()}</span>
          </div>
        </div>

        {/* Tombol navigasi */}
        <div className="flex justify-center gap-4">
          <button
            onClick={() => navigate("/order")}
            className="bg-[#6F4E37] text-white px-4 py-2 rounded shadow-md hover:shadow-lg hover:bg-[#5c3f2d] active:scale-95 transition"
          >
            Order More
          </button>
          <button
            onClick={() => navigate("/check-order")}
            className="bg-[#d4b998] text-[#3b2a1a] px-4 py-2 rounded shadow-md hover:shadow-lg hover:bg-[#c8a97e] active:scale-95 transition"
          >
            Check My Order
          </button>
        </div>
      </div>
    </div>
  );
}
