// src/pages/Order.jsx
import { useState } from "react";
import Header from "../components/Header";
import MenuSection from "../components/MenuSection";
import Footer from "../components/Footer";

const drinks = [
  { id: 1, name: "Espresso", price: 15000, image: "/drink1.jpg" },
  { id: 2, name: "Latte", price: 20000, image: "/drink2.jpg" },
];

const foods = [
  { id: 3, name: "Croissant", price: 12000, image: "/food1.jpg" },
  { id: 4, name: "Sandwich", price: 18000, image: "/food2.jpg" },
];

export default function Order() {
  const [tableNumber, setTableNumber] = useState("");
  const [selectedItems, setSelectedItems] = useState([]);

  // Tambah 1 qty
  const handleAddItem = (item) => {
    const existing = selectedItems.find((i) => i.id === item.id);
    if (existing) {
      setSelectedItems((prev) =>
        prev.map((i) =>
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        )
      );
    } else {
      setSelectedItems((prev) => [...prev, { ...item, quantity: 1 }]);
    }
  };

  // Kurangi 1 qty
  const handleRemoveItem = (itemId) => {
    const existing = selectedItems.find((i) => i.id === itemId);
    if (!existing) return;

    if (existing.quantity === 1) {
      setSelectedItems((prev) => prev.filter((i) => i.id !== itemId));
    } else {
      setSelectedItems((prev) =>
        prev.map((i) =>
          i.id === itemId ? { ...i, quantity: i.quantity - 1 } : i
        )
      );
    }
  };

  const getQty = (id) => {
    const item = selectedItems.find((i) => i.id === id);
    return item ? item.qty : 0;
  };

  const totalItems = selectedItems.reduce(
    (acc, item) => acc + item.quantity,
    0
  );
  const totalPrice = selectedItems.reduce(
    (acc, item) => acc + item.quantity * item.price,
    0
  );

  const handleProceed = () => {
    // nanti arahkan ke halaman pembayaran
    alert("Lanjut ke pembayaran");
  };

  return (
    <div className="pt-20 pb-24 px-4 bg-cream min-h-screen">
      <Header tableNumber={tableNumber} setTableNumber={setTableNumber} />
      <MenuSection
        title="Minuman"
        items={drinks}
        onAdd={handleAddItem}
        onRemove={handleRemoveItem}
        getQty={getQty}
      />

      <MenuSection
        title="Makanan"
        items={foods}
        onAdd={handleAddItem}
        onRemove={handleRemoveItem}
        getQty={getQty}
      />

      <Footer
        total={total}
        count={selectedItems.reduce((sum, item) => sum + item.qty, 0)}
        disabled={!tableNumber || selectedItems.length === 0}
        onProceed={handleProceed}
      />
    </div>
  );
}
