import React, { useEffect, useState } from "react";
import {
  getMenus,
  addMenu,
  updateMenu,
  deleteMenu,
  toggleAvailability,
  updateAvailability,
} from "../services/menuService";
import HeaderAdmin from "../components/HeaderAdmin";
import Sidebar from "../components/Sidebar";

const MenuAdmin = () => {
  const [menus, setMenus] = useState([]);
  const [editingMenu, setEditingMenu] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    price: "",
    image: "",
    category: "",
    available: true,
  });
  const [addingMenu, setAddingMenu] = useState(false);
  const [newMenuData, setNewMenuData] = useState({
    name: "",
    price: "",
    image: "",
    category: "",
    available: true,
  });

  useEffect(() => {
    fetchMenus();
  }, []);

  const fetchMenus = async () => {
    try {
      const data = await getMenus();
      setMenus(data);
    } catch (err) {
      console.error("Gagal ambil data menu", err);
    }
  };

  // Toggle availability
  const handleAvailability = async (menuId, currentAvailable) => {
    try {
      const updatedMenu = await updateAvailability(menuId, !currentAvailable);
      setMenus((prevMenus) =>
        prevMenus.map((menu) =>
          menu.id === menuId ? { ...menu, ...updatedMenu } : menu
        )
      );
    } catch (err) {
      console.error("Gagal update availability:", err);
    }
  };

  // Edit menu
  const handleEdit = (menu) => {
    setEditingMenu(menu);
    setFormData({
      name: menu.name,
      price: menu.price,
      image: menu.image,
      category: menu.category,
      available: menu.available,
    });
  };

  const handleSave = async () => {
    try {
      await updateMenu(editingMenu.id, formData);
      setEditingMenu(null);
      fetchMenus();
    } catch (err) {
      console.error("Gagal update menu", err);
    }
  };

  // Tambah menu
  const handleAddMenu = async () => {
    try {
      await addMenu(newMenuData);
      setAddingMenu(false);
      fetchMenus();
      setNewMenuData({
        name: "",
        price: "",
        image: "",
        category: "",
        available: true,
      });
    } catch (err) {
      console.error("Gagal tambah menu:", err);
    }
  };

  // Delete menu
  const handleDelete = async (menuId) => {
    if (window.confirm("Apakah yakin ingin menghapus menu ini?")) {
      try {
        await deleteMenu(menuId);
        fetchMenus();
      } catch (err) {
        console.error("Gagal hapus menu:", err);
      }
    }
  };

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1 ml-60">
        <HeaderAdmin />
        <div className="p-8 min-h-screen bg-[#fdfaf6]">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-[#5c3d2e]">Menu</h1>
            <button
              onClick={() => setAddingMenu(true)}
              className="px-4 py-2 bg-[#5c3d2e] text-white rounded hover:bg-[#3b2f2f]"
            >
              Tambah Menu
            </button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {menus.map((menu) => (
              <div
                key={menu.id}
                className="bg-white shadow rounded-xl p-4 hover:shadow-lg transition"
              >
                <img
                  src={menu.image}
                  alt={menu.name}
                  className="w-full h-40 object-cover rounded-md mb-3"
                />
                <h3 className="font-semibold text-lg text-[#3b2f2f]">
                  {menu.name}
                </h3>
                <p className="text-sm text-gray-600 mb-1">Rp {menu.price}</p>
                <p className="text-sm text-gray-600 mb-2">
                  Kategori: {menu.category}
                </p>

                <div className="flex justify-between items-center mt-2">
                  <button
                    onClick={() => handleEdit(menu)}
                    className="px-3 py-1 bg-[#e6d5c3] rounded text-sm text-[#3b2f2f] hover:bg-[#d9c4ad]"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(menu.id)}
                    className="px-3 py-1 bg-red-500 text-white rounded text-sm hover:bg-red-600"
                  >
                    Hapus
                  </button>
                  <button
                    onClick={() => handleAvailability(menu.id, menu.available)}
                    className={`px-3 py-1 rounded font-semibold transition ${
                      menu.available
                        ? "bg-green-500 text-white hover:bg-green-600"
                        : "bg-gray-300 text-gray-700 hover:bg-gray-400"
                    }`}
                  >
                    {menu.available ? "Available" : "Unavailable"}
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* Modal Edit Menu */}
          {editingMenu && (
            <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center">
              <div className="bg-white p-6 rounded-xl shadow-md w-96">
                <h2 className="text-lg font-semibold mb-4 text-[#5c3d2e]">
                  Edit Menu
                </h2>
                <input
                  type="text"
                  placeholder="Nama"
                  value={formData.name}
                  onChange={(e) =>
                    setFormData({ ...formData, name: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <input
                  type="number"
                  placeholder="Harga"
                  value={formData.price}
                  onChange={(e) =>
                    setFormData({ ...formData, price: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <input
                  type="text"
                  placeholder="URL Gambar"
                  value={formData.image}
                  onChange={(e) =>
                    setFormData({ ...formData, image: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <input
                  type="text"
                  placeholder="Kategori"
                  value={formData.category}
                  onChange={(e) =>
                    setFormData({ ...formData, category: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <div className="flex items-center space-x-2 mb-3">
                  <input
                    type="checkbox"
                    checked={formData.available}
                    onChange={(e) =>
                      setFormData({ ...formData, available: e.target.checked })
                    }
                  />
                  <span>Available</span>
                </div>
                <div className="flex justify-end gap-2">
                  <button
                    onClick={() => setEditingMenu(null)}
                    className="px-3 py-1 bg-gray-300 rounded text-sm"
                  >
                    Batal
                  </button>
                  <button
                    onClick={handleSave}
                    className="px-3 py-1 bg-[#5c3d2e] text-white rounded text-sm hover:bg-[#3b2f2f]"
                  >
                    Simpan
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Modal Tambah Menu */}
          {addingMenu && (
            <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center">
              <div className="bg-white p-6 rounded-xl shadow-md w-96">
                <h2 className="text-lg font-semibold mb-4 text-[#5c3d2e]">
                  Tambah Menu
                </h2>
                <input
                  type="text"
                  placeholder="Nama"
                  value={newMenuData.name}
                  onChange={(e) =>
                    setNewMenuData({ ...newMenuData, name: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <input
                  type="number"
                  placeholder="Harga"
                  value={newMenuData.price}
                  onChange={(e) =>
                    setNewMenuData({ ...newMenuData, price: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <input
                  type="text"
                  placeholder="URL Gambar"
                  value={newMenuData.image}
                  onChange={(e) =>
                    setNewMenuData({ ...newMenuData, image: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <input
                  type="text"
                  placeholder="Kategori"
                  value={newMenuData.category}
                  onChange={(e) =>
                    setNewMenuData({ ...newMenuData, category: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <div className="flex items-center space-x-2 mb-3">
                  <input
                    type="checkbox"
                    checked={newMenuData.available}
                    onChange={(e) =>
                      setNewMenuData({
                        ...newMenuData,
                        available: e.target.checked,
                      })
                    }
                  />
                  <span>Available</span>
                </div>
                <div className="flex justify-end gap-2">
                  <button
                    onClick={() => setAddingMenu(false)}
                    className="px-3 py-1 bg-gray-300 rounded text-sm"
                  >
                    Batal
                  </button>
                  <button
                    onClick={handleAddMenu}
                    className="px-3 py-1 bg-[#5c3d2e] text-white rounded text-sm hover:bg-[#3b2f2f]"
                  >
                    Tambah
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MenuAdmin;
