// src/components/Header.jsx
import { useState } from "react";
import { FaBars } from "react-icons/fa";
import logo from "../assets/Logo.png";

export default function Header({ tableNumber, setTableNumber }) {
  const [showMenu, setShowMenu] = useState(false);

  return (
    <header className="fixed top-0 left-0 right-0 bg-[#f5efe6] shadow z-50 flex items-center justify-between px-4 py-3">
      <input
        type="number"
        value={tableNumber}
        onChange={(e) => {
          const value = e.target.value;
          if (value.length <= 2) {
            setTableNumber(value.replace(/\D/, "")); // Hanya angka
          }
        }}
        placeholder="No Meja"
        className="px-2 py-1 rounded border text-sm"
      />

      <div className="flex items-center gap-2">
        <div className="w-10 h-10 rounded-full bg-[#6F4E37] flex items-center justify-center">
          <img
            src={logo}
            alt="Mercy's Coffee Logo"
            className="w-6 h-6 object-contain"
          />
        </div>
        <h1 className="text-xl font-bold text-[#6F4E37]">Mercyâ€™s Coffee</h1>
      </div>

      <div className="relative">
        {/* Tombol Hamburger */}
        <button
          onClick={() => setShowMenu(!showMenu)}
          className={`transition-transform duration-300 ${
            showMenu ? "scale-90 rotate-90" : "scale-100"
          }`}
        >
          <FaBars className="text-coffee text-xl" />
        </button>

        {/* Dropdown Menu */}
        {showMenu && (
          <div
            className="absolute right-0 mt-2 w-44 bg-white shadow-lg border rounded 
                     animate-fadeSlide z-50"
          >
            <a
              href="/"
              className="block px-4 py-2 hover:bg-cream transition-colors duration-200"
            >
              Halaman Pemesanan
            </a>
            <a
              href="/check-order"
              className="block px-4 py-2 hover:bg-cream transition-colors duration-200"
            >
              Halaman Order
            </a>
          </div>
        )}
      </div>
    </header>
  );
}
