import React, { useEffect, useState } from "react";
import { getOrders, updateOrderStatus } from "../services/orderService";
import HeaderAdmin from "../components/HeaderAdmin";
import Sidebar from "../components/Sidebar";

const OrdersAdmin = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const data = await getOrders();
      setOrders(data);
    } catch (err) {
      console.error("Gagal ambil data orders", err);
    }
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      await updateOrderStatus(id, { status: newStatus });
      fetchOrders();
    } catch (err) {
      console.error("Gagal update status order", err);
    }
  };

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1 ml-60">
        <HeaderAdmin />
        <div className="p-6">
          <h2 className="text-2xl font-bold text-brown mb-4">Orders</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white shadow rounded-lg">
              <thead className="bg-cream text-brown">
                <tr>
                  <th className="px-4 py-2 text-left">Order ID</th>
                  <th className="px-4 py-2">Menu & Jumlah</th>
                  <th className="px-4 py-2">Waktu</th>
                  <th className="px-4 py-2">Status</th>
                  <th className="px-4 py-2">Bukti Pembayaran</th>
                  <th className="px-4 py-2">Aksi</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((order) => (
                  <tr key={order.id} className="border-t">
                    <td className="px-4 py-2">{order.orderId}</td>
                    <td className="px-4 py-2">{order.items}</td>
                    <td className="px-4 py-2">{order.createdAt}</td>
                    <td className="px-4 py-2">
                      <span
                        className={`px-2 py-1 rounded text-sm ${
                          order.status === "Pending"
                            ? "bg-yellow-200 text-yellow-700"
                            : "bg-green-200 text-green-700"
                        }`}
                      >
                        {order.status}
                      </span>
                    </td>
                    <td className="px-4 py-2">
                      {order.paymentProof ? (
                        <a
                          href={order.paymentProof}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-blue-600 underline"
                        >
                          Lihat
                        </a>
                      ) : (
                        "-"
                      )}
                    </td>
                    <td className="px-4 py-2">
                      <button
                        onClick={() =>
                          handleStatusChange(order.id, order.status)
                        }
                        className="px-3 py-1 bg-choco text-white rounded hover:bg-coffee transition"
                      >
                        Switch
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrdersAdmin;
