import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Payment({ selectedItems, totalPrice }) {
  const navigate = useNavigate();
  const [paymentProof, setPaymentProof] = useState(null);

  // Order ID otomatis
  const orderId = `MC${Math.floor(100000 + Math.random() * 900000)}`;

  const handleConfirm = () => {
    if (!paymentProof) return;
    // Kirim ke halaman sukses pembayaran
    navigate("/payment-success", { state: { orderId } });
  };

  return (
    <div className="min-h-screen bg-[#f5efe6] px-4 py-6">
      {/* Tombol Back */}
      <button
        onClick={() => {
          if (window.confirm("Apakah Anda yakin ingin kembali?")) {
            navigate("/order");
          }
        }}
        className="mb-4"
      >
        <span className="text-2xl font-bold text-black">‚Üê</span>
      </button>

      <h1 className="text-xl font-bold text-center text-coffee mb-6">
        Pembayaran
      </h1>

      {/* List Menu */}
      <div className="bg-white p-4 rounded-lg shadow mb-4">
        {selectedItems.map((item) => (
          <div
            key={item.id}
            className="flex justify-between border-b border-gray-200 py-2"
          >
            <span>
              {item.name} x {item.quantity}
            </span>
            <span>Rp {item.price.toLocaleString()}</span>
          </div>
        ))}
      </div>

      {/* Total */}
      <div className="bg-white p-4 rounded-lg shadow mb-6">
        <p className="font-bold">Total: Rp {totalPrice.toLocaleString()}</p>
      </div>

      {/* QRIS */}
      <div className="bg-white p-4 rounded-lg shadow mb-6 text-center">
        <p className="font-bold mb-2">Scan QRIS untuk membayar</p>
        <img src="/qris.png" alt="QRIS" className="w-48 mx-auto" />
      </div>

      {/* Upload Bukti */}
      <div className="bg-white p-4 rounded-lg shadow mb-6">
        <p className="mb-2">Upload Bukti Pembayaran:</p>
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setPaymentProof(e.target.files[0])}
          className="block w-full border border-gray-300 rounded p-2"
        />
      </div>

      {/* Tombol Confirm */}
      <button
        disabled={!paymentProof}
        onClick={handleConfirm}
        className={`w-full py-3 rounded-lg text-white ${
          paymentProof ? "bg-coffee hover:bg-opacity-80" : "bg-gray-400"
        }`}
      >
        Confirm Payment
      </button>
    </div>
  );
}
