import React, { useEffect, useState } from "react";
import { getOrders, updateOrderStatus } from "../services/orderService";
import HeaderAdmin from "../components/HeaderAdmin";
import Sidebar from "../components/Sidebar";

export default function OrdersAdmin() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  // Ambil data dari backend
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const data = await getOrders();
        setOrders(data);
      } catch (err) {
        console.error("Gagal ambil data orders:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, []);

  // Ubah status order
  const handleStatusChange = async (id, status) => {
    try {
      await updateOrderStatus(id, status);
      setOrders((prev) =>
        prev.map((order) => (order.id === id ? { ...order, status } : order))
      );
    } catch (err) {
      console.error("Gagal update status:", err);
    }
  };

  const OrdersAdmin = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
      fetchOrders();
    }, []);

    const fetchOrders = async () => {
      try {
        const data = await getOrders();
        setOrders(data);
      } catch (err) {
        console.error("Gagal ambil data orders", err);
      }
    };

    const handleStatusChange = async (id, newStatus) => {
      try {
        await updateOrderStatus(id, { status: newStatus });
        fetchOrders();
      } catch (err) {
        console.error("Gagal update status order", err);
      }
    };

    return (
      <div className="flex">
        <Sidebar />
        <div className="flex-1 ml-60">
          <HeaderAdmin />
          <div className="p-8 min-h-screen bg-[#fdfaf6]">
            <h1 className="text-2xl font-bold text-[#5c3d2e] mb-6">Orders</h1>

            <div className="overflow-x-auto bg-white shadow rounded-xl p-6">
              <table className="w-full text-left border-collapse">
                <thead>
                  <tr className="bg-[#f0e5d8] text-[#3b2f2f]">
                    <th className="px-4 py-2 text-sm">Order ID</th>
                    <th className="px-4 py-2 text-sm">No Meja</th>
                    <th className="px-4 py-2 text-sm">Total</th>
                    <th className="px-4 py-2 text-sm">Status</th>
                    <th className="px-4 py-2 text-sm">Bukti</th>
                    <th className="px-4 py-2 text-sm">Tanggal</th>
                  </tr>
                </thead>
                <tbody>
                  {orders.length > 0 ? (
                    orders.map((order) => (
                      <tr
                        key={order.id}
                        className="border-b hover:bg-[#fdf6f0] transition"
                      >
                        <td className="px-4 py-2 text-sm">{order.orderId}</td>
                        <td className="px-4 py-2 text-sm">
                          {order.tableNumber}
                        </td>
                        <td className="px-4 py-2 text-sm">
                          Rp {order.totalPrice}
                        </td>
                        <td className="px-4 py-2 text-sm">
                          <select
                            value={order.status}
                            onChange={(e) =>
                              handleStatusChange(order.id, e.target.value)
                            }
                            className="px-2 py-1 border rounded text-sm"
                          >
                            <option value="Pending">Pending</option>
                            <option value="Ongoing">Ongoing</option>
                          </select>
                        </td>
                        <td className="px-4 py-2 text-sm">
                          {order.paymentProof ? (
                            <a
                              href={order.paymentProof}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-600 underline text-sm"
                            >
                              Lihat
                            </a>
                          ) : (
                            <span className="text-gray-400 text-sm">-</span>
                          )}
                        </td>
                        <td className="px-4 py-2 text-sm">
                          {new Date(order.createdAt).toLocaleString("id-ID")}
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td
                        colSpan="6"
                        className="text-center text-gray-500 py-4 text-sm"
                      >
                        Belum ada order
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  };
}
