import api from "./api";

// Ambil semua order (admin)
// export const getOrders = async () => {
//   const res = await api.get("http://localhost:5000/api/orders");
//   return res.data;
// };

export const getOrders = async () => {
  const [rows] = await db.query("SELECT * FROM orders");
  return rows;
};

// Ambil order berdasarkan ID (user)
export const getOrderById = async (orderId) => {
  const res = await api.get(`http://localhost:5000/api/orders/${orderId}`);
  return res.data;
};

// Buat order baru (user payment)
export const createOrder = async (orderData) => {
  const res = await api.post("http://localhost:5000/api/orders", orderData);
  return res.data;
};

// Update status order (admin)
export const updateOrderStatus = async (orderId, status) => {
  const res = await api.patch(`http://localhost:5000/api/orders/${orderId}`, {
    status,
  });
  return res.data;
};
