export default function MenuSection({
  id,
  title,
  items,
  onAdd,
  onRemove,
  getQty,
}) {
  return (
    <div className="mt-28" id={id}>
      {" "}
      {/* mt-28 agar tidak tertutup kategori tab */}
      <h2 className="text-xl font-bold mb-4 text-center text-[#6b4c32]">
        {title}
      </h2>
      <div className="grid grid-cols-2 gap-4">
        {items.map((item) => {
          const quantity = getQty(item.id);

          return (
            <div
              key={item.id}
              className="bg-[#fdfaf5] p-4 rounded shadow text-center border border-[#e0d6c3]"
            >
              <img
                src={item.image}
                alt={item.name}
                className="w-full h-32 object-cover rounded"
              />
              <h3 className="mt-2 font-semibold text-[#6b4c32]">{item.name}</h3>
              <p className="text-sm text-[#a27c5c]">
                Rp {item.price.toLocaleString("id-ID")}
              </p>

              <div className="flex items-center justify-center mt-2 gap-2">
                {quantity > 0 && (
                  <button
                    onClick={() => onRemove(item.id)}
                    className="px-2 py-1 bg-red-500 text-white rounded"
                  >
                    -
                  </button>
                )}
                {quantity > 0 && (
                  <span className="text-[#6b4c32]">{quantity}</span>
                )}
                <button
                  onClick={() => onAdd(item)}
                  className="px-2 py-1 bg-green-600 text-white rounded"
                >
                  +
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
