import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

export default function Landing() {
  const [showLogin, setShowLogin] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [form, setForm] = useState({ username: "", password: "" });
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [clickCount, setClickCount] = useState(0);

  const handleLogoClick = () => {
    setClickCount((prev) => prev + 1);
    if (clickCount + 1 === 3) {
      setShowLogin(true);
      setClickCount(0); // reset
    }
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (username === "admin" && password === "9987") {
      navigate("/admin/dashboard");
    } else {
      setError("Username atau password salah!");
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
      className="pt-20 pb-24 px-4 bg-cream min-h-screen"
    >
      <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-cream via-white to-amber-50 px-4">
        {/* Logo */}
        <h1
          onClick={handleLogoClick}
          className="text-6xl font-extrabold text-choco mb-4 cursor-pointer select-none"
        >
          Mercy’s Coffee
        </h1>
        <p className="text-lg text-gray-700 italic mb-10">
          Nikmati setiap tegukan dengan suasana yang hangat ☕
        </p>

        {/* Hero Illustration */}
        <img
          src="/coffee-cup.png"
          alt="Coffee Cup"
          className="w-52 mb-8 drop-shadow-lg"
        />

        {/* Button User */}
        <button
          onClick={() => (window.location.href = "/menu")}
          className="bg-choco hover:bg-darkchoco text-white px-8 py-4 rounded-full text-xl font-semibold shadow-md hover:scale-105 transition"
        >
          Pesan Sekarang
        </button>

        {/* Modal Login Admin */}
        {showLogin && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center">
            <div className="bg-white rounded-lg shadow-lg p-6 w-80">
              <h2 className="text-xl font-bold text-choco mb-4 text-center">
                Admin Login
              </h2>
              <input
                type="text"
                placeholder="Username"
                className="border w-full px-3 py-2 rounded mb-3"
                value={form.username}
                onChange={(e) => setForm({ ...form, username: e.target.value })}
              />
              <input
                type="password"
                placeholder="Password"
                className="border w-full px-3 py-2 rounded mb-3"
                value={form.password}
                onChange={(e) => setForm({ ...form, password: e.target.value })}
              />
              {error && <p className="text-red-500 text-sm mb-2">{error}</p>}
              <div className="flex justify-between">
                <button
                  onClick={handleLogin}
                  className="bg-choco text-white px-4 py-2 rounded hover:bg-darkchoco"
                >
                  Login
                </button>
                <button
                  onClick={() => setShowLogin(false)}
                  className="text-gray-600 hover:underline"
                >
                  Batal
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </motion.div>
  );
}
