import { useLocation, useNavigate } from "react-router-dom";

export default function Payment() {
  const { state } = useLocation();
  const navigate = useNavigate();

  if (!state) {
    return (
      <div className="p-4">
        <p>Data pembayaran tidak ditemukan.</p>
        <button
          onClick={() => navigate("/")}
          className="mt-4 px-4 py-2 bg-coffee text-white rounded"
        >
          Kembali ke Menu
        </button>
      </div>
    );
  }

  const { tableNumber, selectedItems, totalItems, totalPrice } = state;

  const handleConfirmPayment = () => {
    alert("Pembayaran berhasil! Terima kasih telah memesan di Mercy’s Coffee.");
    navigate("/");
  };

  return (
    <div className="pt-20 px-4 pb-24 bg-cream min-h-screen">
      <h1 className="text-2xl font-bold text-center mb-6 text-coffee">
        Pembayaran
      </h1>

      {/* Card informasi pesanan */}
      <div className="bg-white rounded-lg shadow p-4 mb-6">
        <p className="mb-2">
          No Meja: <strong>{tableNumber}</strong>
        </p>
        <p className="mb-4">
          Total Item: <strong>{totalItems}</strong>
        </p>

        <ul className="divide-y divide-gray-200">
          {selectedItems.map((item) => (
            <li key={item.id} className="py-2 flex justify-between">
              <span>
                {item.name} x {item.quantity}
              </span>
              <span>Rp {(item.price * item.quantity).toLocaleString()}</span>
            </li>
          ))}
        </ul>

        <div className="mt-4 flex justify-between font-bold">
          <span>Total Harga:</span>
          <span>Rp {totalPrice.toLocaleString()}</span>
        </div>
      </div>

      {/* QRIS Section */}
      <div className="bg-white rounded-lg shadow p-4 text-center">
        <p className="font-semibold mb-2 text-coffee">
          Scan QRIS untuk Membayar
        </p>
        <img
          src="/qris-sample.png" // ganti dengan QRIS Mercy’s Coffee
          alt="QRIS Mercy’s Coffee"
          className="w-48 h-48 mx-auto mb-4 border border-gray-300 rounded"
        />
        <p className="text-sm text-gray-500">
          Setelah pembayaran, klik tombol konfirmasi di bawah.
        </p>
      </div>

      {/* Tombol konfirmasi */}
      <button
        onClick={handleConfirmPayment}
        className="mt-6 w-full py-3 bg-green-600 text-white rounded hover:bg-green-700 shadow-lg transition-all duration-200"
      >
        Konfirmasi Pembayaran
      </button>
    </div>
  );
}
