// src/components/MenuSection.jsx
export default function MenuSection({ title, items, onSelect }) {
  return (
    <div className="mb-8">
      <h2 className="text-coffee text-xl font-bold mb-3">{title}</h2>
      <div className="grid grid-cols-2 gap-4">
        {items.map((item) => (
          <div key={item.id} className="bg-white border rounded p-4 shadow">
            <img
              src={item.image}
              alt={item.name}
              className="w-full h-24 object-cover rounded mb-2"
            />
            <h3 className="text-coffee font-semibold">{item.name}</h3>
            <p className="text-sm text-gray-600">
              Rp{item.price.toLocaleString()}
            </p>
            <button
              onClick={() => onSelect(item)}
              className="mt-2 bg-coffee text-white px-3 py-1 rounded hover:bg-opacity-80"
            >
              Pilih
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
