import { useLocation, useNavigate } from "react-router-dom";

export default function Payment() {
  const location = useLocation();
  const navigate = useNavigate();

  // Ambil state dari navigate (jika ada)
  const state = location.state || {};
  const { tableNumber, selectedItems, totalItems, totalPrice } = state;

  // Kalau tidak ada data, tampilkan pesan
  if (!tableNumber || !selectedItems) {
    return (
      <div className="p-4">
        <p>Data pembayaran tidak ditemukan.</p>
        <button
          onClick={() => navigate("/")}
          className="mt-4 px-4 py-2 bg-coffee text-white rounded"
        >
          Kembali ke Menu
        </button>
      </div>
    );
  }

  return (
    <div className="pt-20 px-4 bg-cream min-h-screen">
      <h1 className="text-2xl font-bold text-center mb-6">Pembayaran</h1>

      <div className="bg-white rounded-lg shadow p-4">
        <p className="mb-2">
          No Meja: <strong>{tableNumber}</strong>
        </p>
        <p className="mb-4">
          Total Item: <strong>{totalItems}</strong>
        </p>

        <ul className="divide-y divide-gray-200">
          {selectedItems.map((item) => (
            <li key={item.id} className="py-2 flex justify-between">
              <span>
                {item.name} x {item.quantity}
              </span>
              <span>Rp {(item.price * item.quantity).toLocaleString()}</span>
            </li>
          ))}
        </ul>

        <div className="mt-4 flex justify-between font-bold">
          <span>Total Harga:</span>
          <span>Rp {totalPrice.toLocaleString()}</span>
        </div>
      </div>

      <button
        onClick={() => {
          alert("Pembayaran berhasil! Terima kasih.");
          navigate("/");
        }}
        className="mt-6 w-full py-2 bg-green-600 text-white rounded hover:bg-green-700"
      >
        Bayar Sekarang
      </button>
    </div>
  );
}
