import api from "./api";
import axios from "axios";

const API_URL = "http://localhost:5000/api/menu";

// ✅ Ambil semua menu
export const getMenus = async () => {
  const res = await axios.get("/menu");
  return res.data;
};

// ✅ Tambah menu baru
export const addMenu = async (menuData) => {
  const res = await api.post("/menus", menuData, {
    headers: { "Content-Type": "multipart/form-data" }, // karena ada upload foto
  });
  return res.data;
};

// ✅ Edit menu (nama, harga, foto)
export const updateMenu = async (id, menuData) => {
  const res = await api.put(`/menus/${id}`, menuData, {
    headers: { "Content-Type": "multipart/form-data" },
  });
  return res.data;
};

// ✅ Hapus menu
export const deleteMenu = async (id) => {
  const res = await api.delete(`/menus/${id}`);
  return res.data;
};

// ✅ Switch available / not
export const toggleAvailability = async (id, available) => {
  const res = await api.patch(`/menus/${id}`, { available });
  return res.data;
};
