import React, { useEffect, useState } from "react";
import HeaderAdmin from "../components/HeaderAdmin";
import Sidebar from "../components/Sidebar";
import { getOrders } from "../services/orderService";
import { getMenus } from "../services/menuService";
import {
  ClipboardDocumentListIcon,
  ClockIcon,
  CheckCircleIcon,
  Squares2X2Icon,
} from "@heroicons/react/24/outline";

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    pendingOrders: 0,
    ongoingOrders: 0,
    totalMenu: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const orders = await getOrders();
        const menus = await getMenus();

        setStats({
          totalOrders: orders.length,
          pendingOrders: orders.filter((o) => o.status === "Pending").length,
          ongoingOrders: orders.filter((o) => o.status === "Ongoing").length,
          totalMenu: menus.length,
        });
      } catch (err) {
        console.error("Gagal ambil data dashboard", err);
      }
    };

    fetchStats();
  }, []);

  const cards = [
    {
      title: "Total Orders",
      value: stats.totalOrders,
      icon: <ClipboardDocumentListIcon className="h-8 w-8 text-[#a47148]" />,
      bg: "bg-[#f0e5d8]",
    },
    {
      title: "Pending Orders",
      value: stats.pendingOrders,
      icon: <ClockIcon className="h-8 w-8 text-yellow-600" />,
      bg: "bg-yellow-100",
    },
    {
      title: "Ongoing Orders",
      value: stats.ongoingOrders,
      icon: <CheckCircleIcon className="h-8 w-8 text-green-600" />,
      bg: "bg-green-100",
    },
    {
      title: "Total Menu",
      value: stats.totalMenu,
      icon: <Squares2X2Icon className="h-8 w-8 text-[#5c3d2e]" />,
      bg: "bg-[#e6d5c3]",
    },
  ];

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1 ml-60">
        <HeaderAdmin />
        <div className="ml-60 p-8 min-h-screen bg-[#fdfaf6]">
          <h1 className="text-2xl font-bold text-[#5c3d2e] mb-6">Dashboard</h1>

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {cards.map((card, index) => (
              <div
                key={index}
                className={`${card.bg} p-6 rounded-xl shadow hover:shadow-lg transition-all duration-200 flex items-center justify-between`}
              >
                <div>
                  <p className="text-sm text-gray-600">{card.title}</p>
                  <h2 className="text-2xl font-bold text-[#3b2f2f]">
                    {card.value}
                  </h2>
                </div>
                {card.icon}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
