import React, { useEffect, useState } from "react";
import { getMenus, updateMenu } from "../services/menuService";

const MenuAdmin = () => {
  const [menus, setMenus] = useState([]);
  const [editingMenu, setEditingMenu] = useState(null);
  const [formData, setFormData] = useState({ name: "", price: "", image: "" });

  useEffect(() => {
    fetchMenus();
  }, []);

  const fetchMenus = async () => {
    try {
      const data = await getMenus();
      setMenus(data);
    } catch (err) {
      console.error("Gagal ambil data menu", err);
    }
  };

  const handleAvailability = async (menu) => {
    try {
      await updateMenu(menu.id, { available: !menu.available });
      fetchMenus();
    } catch (err) {
      console.error("Gagal update availability", err);
    }
  };

  const handleEdit = (menu) => {
    setEditingMenu(menu);
    setFormData({
      name: menu.name,
      price: menu.price,
      image: menu.image,
    });
  };

  const handleSave = async () => {
    try {
      await updateMenu(editingMenu.id, formData);
      setEditingMenu(null);
      fetchMenus();
    } catch (err) {
      console.error("Gagal update menu", err);
    }
  };

  return (
    <div className="ml-60 p-8 min-h-screen bg-[#fdfaf6]">
      <h1 className="text-2xl font-bold text-[#5c3d2e] mb-6">Menu</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {menus.map((menu) => (
          <div
            key={menu.id}
            className="bg-white shadow rounded-xl p-4 hover:shadow-lg transition"
          >
            <img
              src={menu.image}
              alt={menu.name}
              className="w-full h-40 object-cover rounded-md mb-3"
            />
            <h3 className="font-semibold text-lg text-[#3b2f2f]">
              {menu.name}
            </h3>
            <p className="text-sm text-gray-600 mb-2">Rp {menu.price}</p>
            <div className="flex items-center justify-between">
              <button
                onClick={() => handleEdit(menu)}
                className="px-3 py-1 bg-[#e6d5c3] rounded text-sm text-[#3b2f2f] hover:bg-[#d9c4ad]"
              >
                Edit
              </button>
              <button
                onClick={() => handleAvailability(menu)}
                className={`px-3 py-1 rounded text-sm ${
                  menu.available
                    ? "bg-green-200 text-green-700"
                    : "bg-red-200 text-red-700"
                }`}
              >
                {menu.available ? "Available" : "Not Available"}
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal Edit */}
      {editingMenu && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center">
          <div className="bg-white p-6 rounded-xl shadow-md w-96">
            <h2 className="text-lg font-semibold mb-4 text-[#5c3d2e]">
              Edit Menu
            </h2>
            <input
              type="text"
              placeholder="Nama"
              value={formData.name}
              onChange={(e) =>
                setFormData({ ...formData, name: e.target.value })
              }
              className="w-full border px-3 py-2 rounded mb-3"
            />
            <input
              type="number"
              placeholder="Harga"
              value={formData.price}
              onChange={(e) =>
                setFormData({ ...formData, price: e.target.value })
              }
              className="w-full border px-3 py-2 rounded mb-3"
            />
            <input
              type="text"
              placeholder="URL Gambar"
              value={formData.image}
              onChange={(e) =>
                setFormData({ ...formData, image: e.target.value })
              }
              className="w-full border px-3 py-2 rounded mb-4"
            />
            <div className="flex justify-end gap-2">
              <button
                onClick={() => setEditingMenu(null)}
                className="px-3 py-1 bg-gray-300 rounded text-sm"
              >
                Batal
              </button>
              <button
                onClick={handleSave}
                className="px-3 py-1 bg-[#5c3d2e] text-white rounded text-sm hover:bg-[#3b2f2f]"
              >
                Simpan
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MenuAdmin;
