import { useState } from "react";
import { FaBars } from "react-icons/fa";

export default function Header({ tableNumber, setTableNumber }) {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <header className="fixed top-0 left-0 right-0 bg-cream shadow z-50 flex justify-between items-center p-4">
      <input
        type="number"
        placeholder="No Meja"
        value={tableNumber}
        onChange={(e) => setTableNumber(e.target.value)}
        className="w-24 px-2 py-1 border rounded text-choco"
      />
      <div className="text-xl font-bold text-choco">Mercyâ€™s Coffee</div>
      <div className="relative">
        <button onClick={() => setMenuOpen(!menuOpen)} className="text-choco">
          <FaBars size={24} />
        </button>
        {menuOpen && (
          <div className="absolute top-full right-0 mt-2 bg-cream shadow rounded w-48 p-2 space-y-2">
            <a href="/" className="block text-choco">
              Halaman Pemesanan
            </a>
            <a href="/check-order" className="block text-choco">
              Halaman Order
            </a>
          </div>
        )}
      </div>
    </header>
  );
}
