import React, { useEffect, useState } from "react";
import {
  getMenus,
  addMenu,
  updateMenu,
  deleteMenu,
  toggleAvailability,
  updateAvailability,
} from "../services/menuService";
import HeaderAdmin from "../components/HeaderAdmin";
import Sidebar from "../components/Sidebar";

const MenuAdmin = () => {
  const [menus, setMenus] = useState([]);
  const [editingMenu, setEditingMenu] = useState(null);
  const [formData, setFormData] = useState({ name: "", price: "", image: "" });
  const ToggleSwitch = ({ checked, onChange }) => {
    return (
      <label className="relative inline-flex items-center cursor-pointer">
        <input
          type="checkbox"
          checked={checked}
          onChange={(e) => onChange(e.target.checked)}
          className="sr-only"
        />
        <div
          className={`w-11 h-6 rounded-full transition-colors duration-300 ${
            checked ? "bg-green-500" : "bg-gray-300"
          }`}
        ></div>
        <div
          className={`absolute left-1 top-1 w-4 h-4 bg-white rounded-full shadow transform transition-transform duration-300 ${
            checked ? "translate-x-5" : "translate-x-0"
          }`}
        ></div>
      </label>
    );
  };

  useEffect(() => {
    fetchMenus();
  }, []);

  const fetchMenus = async () => {
    try {
      const data = await getMenus();
      setMenus(data);
    } catch (err) {
      console.error("Gagal ambil data menu", err);
    }
  };

  const handleAvailability = async (menuId, newAvailable) => {
    try {
      const updatedMenu = await updateAvailability(menuId, newAvailable);
      setMenus((prevMenus) =>
        prevMenus.map((menu) =>
          menu.id === menuId ? { ...menu, ...updatedMenu } : menu
        )
      );
    } catch (err) {
      console.error("Gagal update availability:", err);
    }
  };

  const handleEdit = (menu) => {
    setEditingMenu(menu);
    setFormData({
      name: menu.name,
      price: menu.price,
      image: menu.image,
      category: menu.category,
      available: menu.available,
    });
  };

  const handleSave = async () => {
    try {
      await updateMenu(editingMenu.id, formData);
      setEditingMenu(null);
      fetchMenus();
    } catch (err) {
      console.error("Gagal update menu", err);
    }
  };

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1 ml-60">
        <HeaderAdmin />
        <div className="p-8 min-h-screen bg-[#fdfaf6]">
          <h1 className="text-2xl font-bold text-[#5c3d2e] mb-6">Menu</h1>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {menus.map((menu) => (
              <div
                key={menu.id}
                className="bg-white shadow rounded-xl p-4 hover:shadow-lg transition"
              >
                <img
                  src={menu.image}
                  alt={menu.name}
                  className="w-full h-40 object-cover rounded-md mb-3"
                />
                <h3 className="font-semibold text-lg text-[#3b2f2f]">
                  {menu.name}
                </h3>
                <p className="text-sm text-gray-600 mb-2">Rp {menu.price}</p>
                <div className="flex items-center justify-between">
                  <button
                    onClick={() => handleEdit(menu)}
                    className="px-3 py-1 bg-[#e6d5c3] rounded text-sm text-[#3b2f2f] hover:bg-[#d9c4ad]"
                  >
                    Edit
                  </button>
                  <td className="px-4 py-2 text-sm">
                    <ToggleSwitch
                      checked={menu.available === 1}
                      onChange={(newValue) =>
                        handleAvailability(menu.id, newValue ? 1 : 0)
                      }
                    />
                  </td>
                </div>
              </div>
            ))}
          </div>

          {/* Modal Edit */}
          {editingMenu && (
            <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center">
              <div className="bg-white p-6 rounded-xl shadow-md w-96">
                <h2 className="text-lg font-semibold mb-4 text-[#5c3d2e]">
                  Edit Menu
                </h2>
                <input
                  type="text"
                  placeholder="Nama"
                  value={formData.name}
                  onChange={(e) =>
                    setFormData({ ...formData, name: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <input
                  type="number"
                  placeholder="Harga"
                  value={formData.price}
                  onChange={(e) =>
                    setFormData({ ...formData, price: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <input
                  type="text"
                  placeholder="Kategori"
                  value={formData.category}
                  onChange={(e) =>
                    setFormData({ ...formData, category: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-3"
                />
                <input
                  type="text"
                  placeholder="URL Gambar"
                  value={formData.image}
                  onChange={(e) =>
                    setFormData({ ...formData, image: e.target.value })
                  }
                  className="w-full border px-3 py-2 rounded mb-4"
                />
                <div className="flex items-center space-x-2 mb-3">
                  <input
                    type="checkbox"
                    checked={formData.available}
                    onChange={(e) =>
                      setFormData({ ...formData, available: e.target.checked })
                    }
                  />
                  <span>Available</span>
                </div>

                <div className="flex justify-end gap-2">
                  <button
                    onClick={() => setEditingMenu(null)}
                    className="px-3 py-1 bg-gray-300 rounded text-sm"
                  >
                    Batal
                  </button>
                  <button
                    onClick={handleSave}
                    className="px-3 py-1 bg-[#5c3d2e] text-white rounded text-sm hover:bg-[#3b2f2f]"
                  >
                    Simpan
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MenuAdmin;
