import api from "./api";

const API_URL = "http://localhost:5000/api/menu";

// ✅ Ambil semua menu
export const getMenus = async () => {
  const res = await api.get("/menu");
  return res.data;
};

// ✅ Tambah menu baru
export const addMenu = async (menuData) => {
  const res = await api.post("/menu", menuData, {
    headers: { "Content-Type": "multipart/form-data" }, // karena ada upload foto
  });
  return res.data;
};

// ✅ Edit menu (nama, harga, foto)
export const updateMenu = async (id, menuData) => {
  const res = await api.put(`/menu/${id}`, menuData, {
    headers: { "Content-Type": "multipart/form-data" },
  });
  return res.data;
};

// ✅ Hapus menu
export const deleteMenu = async (id) => {
  const res = await api.delete(`/menu/${id}`);
  return res.data;
};

// ✅ Switch available / not
export const updateAvailability = async (menuId, available) => {
  const res = await api.patch(`/menu/${menuId}/availability`, { available });
  return res.data;
};
