// src/components/Header.jsx
import { useState } from "react";
import { FaBars } from "react-icons/fa";
import logo from "../assets/Logo.png";

export default function Header({ tableNumber, setTableNumber }) {
  const [showMenu, setShowMenu] = useState(false);

  return (
    <header className="fixed top-0 left-0 right-0 bg-[#f5efe6] shadow z-50 flex items-center justify-between px-4 py-3">
      <input
        type="number"
        value={tableNumber}
        onChange={(e) => {
          const value = e.target.value;
          if (value.length <= 2) {
            setTableNumber(value.replace(/\D/, "")); // Hanya angka
          }
        }}
        placeholder="No Meja"
        className="px-2 py-1 rounded border text-sm"
      />

      <div className="flex items-center gap-2">
        <img
          src={logo}
          alt="Mercy's Coffee Logo"
          className="w-8 h-8 object-contain"
        />
        <h1 className="text-xl font-bold text-[#6F4E37]">Mercyâ€™s Coffee</h1>
      </div>

      <div>
        <button onClick={() => setShowMenu(!showMenu)}>
          <FaBars className="text-coffee text-xl" />
        </button>
        {showMenu && (
          <div className="absolute right-4 mt-2 w-40 bg-white shadow border rounded">
            <a href="/" className="block px-4 py-2 hover:bg-cream">
              Halaman Pemesanan
            </a>
            <a href="/check-order" className="block px-4 py-2 hover:bg-cream">
              Halaman Order
            </a>
          </div>
        )}
      </div>
    </header>
  );
}
