import { useState } from "react";

export default function CheckOrder() {
  const [orderId, setOrderId] = useState("");
  const [status, setStatus] = useState("");
  const [error, setError] = useState("");

  // Simulasi database status order
  const orders = {
    MC123456: "Sedang diproses",
    MC654321: "Pesanan selesai",
  };

  const handleCheck = () => {
    if (orders[orderId.trim()]) {
      setError("");
      setStatus(orders[orderId.trim()]);
    } else {
      setStatus("");
      setError("❌ Order ID tidak ditemukan");
    }
  };

  return (
    <div className="min-h-screen bg-[#f5efe6] flex flex-col items-center justify-center p-6">
      <div className="bg-white p-6 rounded-lg shadow-md w-full max-w-md">
        <h1 className="text-center text-2xl font-bold text-[#6F4E37] mb-4">
          Check My Order
        </h1>

        {/* Input Order ID */}
        <input
          type="text"
          value={orderId}
          onChange={(e) => setOrderId(e.target.value)}
          placeholder="Masukkan Order ID"
          maxLength={8}
          className="w-full border border-[#d3bfa6] rounded px-4 py-2 mb-3 focus:outline-none focus:ring-2 focus:ring-[#6F4E37]"
        />

        {/* Pesan error */}
        {error && <p className="text-red-500 text-sm mb-3">{error}</p>}

        {/* Tombol View My Order */}
        <button
          onClick={handleCheck}
          disabled={!orderId}
          className={`w-full px-4 py-2 rounded text-white transition ${
            orderId
              ? "bg-[#6F4E37] hover:bg-[#5c3f2d] active:scale-95"
              : "bg-gray-400 cursor-not-allowed"
          }`}
        >
          View My Order
        </button>

        {/* Status Order */}
        {status && (
          <div className="mt-4 p-3 border border-[#d3bfa6] rounded bg-[#faf6f1] text-center">
            <p className="font-semibold text-[#6F4E37]">
              Status Pesanan: {status}
            </p>
          </div>
        )}
      </div>
    </div>
  );
}
