import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import Header from "../components/Header";
import MenuSection from "../components/MenuSection";
import Footer from "../components/Footer";

// Data Menu
const drinks = [
  { id: 1, name: "Espresso", price: 15000, image: "/drink1.jpg" },
  { id: 2, name: "Latte", price: 25000, image: "/drink2.jpg" },
  { id: 3, name: "Americano", price: 21000, image: "/drink3.jpg" },
];

const foods = [
  { id: 21, name: "Croissant", price: 12000, image: "/food1.jpg" },
  { id: 22, name: "Sandwich", price: 18000, image: "/food2.jpg" },
  { id: 23, name: "French Fries", price: 20000, image: "/food3.jpg" },
];

export default function Home() {
  const navigate = useNavigate();
  const [tableNumber, setTableNumber] = useState("");
  const [selectedItems, setSelectedItems] = useState([]);
  const [activeTab, setActiveTab] = useState("minuman");

  // Tambah item
  const handleAddItem = (item) => {
    const existing = selectedItems.find((i) => i.id === item.id);
    if (existing) {
      setSelectedItems((prev) =>
        prev.map((i) =>
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        )
      );
    } else {
      setSelectedItems((prev) => [...prev, { ...item, quantity: 1 }]);
    }
  };

  // Kurangi item
  const handleRemoveItem = (itemId) => {
    const existing = selectedItems.find((i) => i.id === itemId);
    if (!existing) return;

    if (existing.quantity === 1) {
      setSelectedItems((prev) => prev.filter((i) => i.id !== itemId));
    } else {
      setSelectedItems((prev) =>
        prev.map((i) =>
          i.id === itemId ? { ...i, quantity: i.quantity - 1 } : i
        )
      );
    }
  };

  // Cek qty item
  const getQty = (id) => {
    const item = selectedItems.find((i) => i.id === id);
    return item ? item.quantity : 0;
  };

  // Hitung total
  const totalItems = selectedItems.reduce(
    (acc, item) => acc + item.quantity,
    0
  );
  const totalPrice = selectedItems.reduce(
    (acc, item) => acc + item.quantity * item.price,
    0
  );

  // Lanjut ke pembayaran
  const handleProceed = () => {
    navigate("/payment", {
      state: {
        tableNumber,
        selectedItems,
        totalItems,
        totalPrice,
      },
    });
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
      className="pt-20 pb-24 px-4 bg-cream min-h-screen"
    >
      <Header tableNumber={tableNumber} setTableNumber={setTableNumber} />

      {/* Tab Pilihan */}
      <div className="flex justify-center gap-4 mb-6">
        <button
          onClick={() => setActiveTab("minuman")}
          className={`px-4 py-2 rounded-full font-medium ${
            activeTab === "minuman"
              ? "bg-[#a47148] text-white"
              : "bg-[#f0e5d8] text-brown"
          }`}
        >
          Minuman
        </button>
        <button
          onClick={() => setActiveTab("makanan")}
          className={`px-4 py-2 rounded-full font-medium ${
            activeTab === "makanan"
              ? "bg-[#a47148] text-white"
              : "bg-[#f0e5d8] text-brown"
          }`}
        >
          Makanan
        </button>
      </div>

      {/* Section Menu */}
      {activeTab === "minuman" && (
        <MenuSection
          title="Minuman"
          items={drinks}
          onAdd={handleAddItem}
          onRemove={handleRemoveItem}
          getQty={getQty}
        />
      )}

      {activeTab === "makanan" && (
        <MenuSection
          title="Makanan"
          items={foods}
          onAdd={handleAddItem}
          onRemove={handleRemoveItem}
          getQty={getQty}
        />
      )}

      {/* Footer Checkout */}
      <Footer
        total={totalPrice}
        count={totalItems}
        disabled={!tableNumber || selectedItems.length === 0}
        onProceed={handleProceed}
      />
    </motion.div>
  );
}
