// src/components/Footer.jsx
import { motion, AnimatePresence } from "framer-motion";

export default function Footer({ total, count, disabled, onProceed }) {
  return (
    <AnimatePresence>
      {count > 0 && (
        <motion.footer
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 40 }}
          transition={{ duration: 0.3 }}
          className="fixed bottom-4 left-1/2 transform -translate-x-1/2 w-[90%] max-w-lg bg-[#f5efe6] border border-[#b68d5a] px-6 py-3 rounded-xl shadow-xl flex justify-between items-center z-50"
        >
          <div>
            <p className="font-bold text-[#6b4c32]">
              Total: Rp{total.toLocaleString("id-ID")}
            </p>
            <p className="text-sm text-[#6b4c32]">{count} item</p>
          </div>
          <button
            disabled={disabled}
            onClick={onProceed}
            className={`px-4 py-2 rounded font-semibold transition duration-200 ${
              disabled
                ? "bg-gray-300 text-gray-600 cursor-not-allowed"
                : "bg-[#6b4c32] text-white hover:opacity-90"
            }`}
          >
            Bayar
          </button>
        </motion.footer>
      )}
    </AnimatePresence>
  );
}
