export default function MenuSection({ title, items, onAdd, onRemove, getQty }) {
  return (
    <div className="mt-8 transition-opacity duration-500 ease-in-out animate-fade-in">
      <h2 className="text-xl font-bold mb-4 text-center text-[#6b4c32]">
        {title}
      </h2>
      <div className="grid grid-cols-2 gap-4">
        {items.map((item) => {
          const quantity = getQty(item.id);

          return (
            <div
              key={item.id}
              className="bg-[#fdf6ec] border border-[#e2cdb2] p-4 rounded-lg shadow-sm text-center"
            >
              <img
                src={item.image}
                alt={item.name}
                className="w-full h-32 object-cover rounded-md"
              />
              <h3 className="mt-2 font-semibold text-[#4a3220] flex items-center justify-center gap-1">
                <span className="text-lg">{item.icon}</span>
                {item.name}
              </h3>
              <p className="text-sm text-gray-500 mb-2">
                Rp {item.price.toLocaleString("id-ID")}
              </p>

              <div className="flex items-center justify-center mt-2 gap-2">
                {quantity > 0 && (
                  <button
                    onClick={() => onRemove(item.id)}
                    className="px-2 py-1 bg-red-500 text-white rounded"
                  >
                    -
                  </button>
                )}
                {quantity > 0 && (
                  <span className="min-w-[20px]">{quantity}</span>
                )}
                <button
                  onClick={() => onAdd(item)}
                  className="px-2 py-1 bg-green-600 text-white rounded"
                >
                  +
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
