import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Landing() {
  const [showLogin, setShowLogin] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [clickCount, setClickCount] = useState(0);

  const handleLogoClick = () => {
    setClickCount((prev) => prev + 1);
    if (clickCount + 1 === 3) {
      setShowLogin(true);
      setClickCount(0); // reset
    }
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (username === "admin" && password === "9987") {
      navigate("/admin/dashboard");
    } else {
      setError("Username atau password salah!");
    }
  };

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-cream relative">
      {/* Judul */}
      <h1
        onClick={handleLogoClick}
        className="text-5xl font-bold text-choco mb-6 text-center"
      >
        Selamat Datang di <br /> Mercy’s Coffee
      </h1>

      <p className="text-lg text-gray-700 mb-10 text-center max-w-md">
        Nikmati pengalaman memesan kopi terbaik dengan nuansa bohemian minimalis
      </p>

      {/* Tombol User */}
      <Link
        to="/user"
        className="px-10 py-4 bg-choco text-white text-lg rounded-xl shadow-lg hover:bg-brown-700 transition transform hover:scale-105"
      >
        ☕ Mulai Pesan Sekarang
      </Link>

      {/* Tombol Admin (disembunyikan di pojok kanan bawah) */}
      <button
        onClick={() => setShowLogin(true)}
        className="absolute bottom-2 right-2 text-xs text-gray-300 hover:text-gray-400"
      >
        v.1.0
      </button>

      {/* Modal Login */}
      {showLogin && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40 z-50">
          <div className="bg-white rounded-lg shadow-lg w-80 p-6">
            <h2 className="text-xl font-bold text-choco mb-4 text-center">
              Admin Login
            </h2>

            <form onSubmit={handleLogin} className="space-y-4">
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-choco"
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-choco"
              />
              {error && (
                <p className="text-red-500 text-sm text-center">{error}</p>
              )}
              <button
                type="submit"
                className="w-full py-2 bg-choco text-white rounded-lg hover:bg-brown-700 transition"
              >
                Login
              </button>
              <button
                type="button"
                onClick={() => setShowLogin(false)}
                className="w-full py-2 mt-2 bg-gray-200 rounded-lg hover:bg-gray-300 transition"
              >
                Batal
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
