// src/pages/Order.jsx
import { useState } from "react";
import Header from "../components/Header";
import MenuSection from "../components/MenuSection";
import Footer from "../components/Footer";

const drinks = [
  { id: 1, name: "Espresso", price: 15000, image: "/drink1.jpg" },
  { id: 2, name: "Latte", price: 20000, image: "/drink2.jpg" },
];

const foods = [
  { id: 3, name: "Croissant", price: 12000, image: "/food1.jpg" },
  { id: 4, name: "Sandwich", price: 18000, image: "/food2.jpg" },
];

export default function Order() {
  const [tableNumber, setTableNumber] = useState("");
  const [selectedItems, setSelectedItems] = useState([]);

  // Tambah 1 qty
  const handleAddQty = (item) => {
    setSelectedItems((prev) => {
      const found = prev.find((i) => i.id === item.id);
      if (found) {
        return prev.map((i) =>
          i.id === item.id ? { ...i, qty: i.qty + 1 } : i
        );
      } else {
        return [...prev, { ...item, qty: 1 }];
      }
    });
  };

  // Kurangi 1 qty
  const handleRemoveQty = (item) => {
    setSelectedItems((prev) => {
      const found = prev.find((i) => i.id === item.id);
      if (found && found.qty > 1) {
        return prev.map((i) =>
          i.id === item.id ? { ...i, qty: i.qty - 1 } : i
        );
      } else {
        return prev.filter((i) => i.id !== item.id);
      }
    });
  };

  const getQty = (id) => {
    const item = selectedItems.find((i) => i.id === id);
    return item ? item.qty : 0;
  };

  const total = selectedItems.reduce((sum, item) => sum + item.price, 0);

  const handleProceed = () => {
    // nanti arahkan ke halaman pembayaran
    alert("Lanjut ke pembayaran");
  };

  return (
    <div className="pt-20 pb-24 px-4 bg-cream min-h-screen">
      <Header tableNumber={tableNumber} setTableNumber={setTableNumber} />
      <MenuSection
        title="Minuman"
        items={drinks}
        onAdd={handleAddQty}
        onRemove={handleRemoveQty}
        getQty={getQty}
      />

      <MenuSection
        title="Makanan"
        items={foods}
        onAdd={handleAddQty}
        onRemove={handleRemoveQty}
        getQty={getQty}
      />

      <Footer
        total={total}
        count={selectedItems.reduce((sum, item) => sum + item.qty, 0)}
        disabled={!tableNumber || selectedItems.length === 0}
        onProceed={handleProceed}
      />
    </div>
  );
}
