export default function MenuSection({ title, items, onAdd, onRemove, getQty }) {
  return (
    <div className="mb-8">
      <h2 className="text-coffee text-xl font-bold mb-3">{title}</h2>
      <div className="grid grid-cols-2 gap-4">
        {items.map((item) => (
          <div key={item.id} className="bg-white border rounded p-4 shadow">
            <img
              src={item.image}
              alt={item.name}
              className="w-full h-24 object-cover rounded mb-2"
            />
            <h3 className="text-coffee font-semibold">{item.name}</h3>
            <p className="text-sm text-gray-600 mb-2">
              Rp{item.price.toLocaleString()}
            </p>

            <div className="flex items-center space-x-2">
              <button
                onClick={() => onRemove(item)}
                className="w-8 h-8 text-lg bg-coffee text-white rounded"
              >
                âˆ’
              </button>
              <span className="w-6 text-center">{getQty(item.id)}</span>
              <button
                onClick={() => onAdd(item)}
                className="w-8 h-8 text-lg bg-coffee text-white rounded"
              >
                +
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
