import React, { useEffect, useState } from "react";
import { getMenus, updateMenu } from "../services/menuService";
import HeaderAdmin from "../components/HeaderAdmin";
import Sidebar from "../components/Sidebar";

const MenuAdmin = () => {
  const [menus, setMenus] = useState([]);
  const [editingMenu, setEditingMenu] = useState(null);
  const [formData, setFormData] = useState({ name: "", price: "", image: "" });

  useEffect(() => {
    fetchMenus();
  }, []);

  const fetchMenus = async () => {
    try {
      const data = await getMenus();
      setMenus(data);
    } catch (err) {
      console.error("Gagal ambil data menu", err);
    }
  };

  const handleAvailability = async (menu) => {
    try {
      await updateMenu(menu.id, { available: !menu.available });
      fetchMenus();
    } catch (err) {
      console.error("Gagal update availability", err);
    }
  };

  const handleEdit = (menu) => {
    setEditingMenu(menu);
    setFormData({
      name: menu.name,
      price: menu.price,
      image: menu.image,
    });
  };

  const handleSave = async () => {
    try {
      await updateMenu(editingMenu.id, formData);
      setEditingMenu(null);
      fetchMenus();
    } catch (err) {
      console.error("Gagal update menu", err);
    }
  };

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1 ml-60">
        <HeaderAdmin />
        <div className="p-6">
          <h2 className="text-2xl font-bold text-brown mb-4">
            Menu Management
          </h2>
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white shadow rounded-lg">
              <thead className="bg-cream text-brown">
                <tr>
                  <th className="px-4 py-2 text-left">Foto</th>
                  <th className="px-4 py-2 text-left">Nama</th>
                  <th className="px-4 py-2">Harga</th>
                  <th className="px-4 py-2">Status</th>
                  <th className="px-4 py-2">Aksi</th>
                </tr>
              </thead>
              <tbody>
                {menus.map((menu) => (
                  <tr key={menu.id} className="border-t">
                    <td className="px-4 py-2">
                      <img
                        src={menu.image}
                        alt={menu.name}
                        className="h-16 w-16 object-cover rounded"
                      />
                    </td>
                    <td className="px-4 py-2">{menu.name}</td>
                    <td className="px-4 py-2">Rp {menu.price}</td>
                    <td className="px-4 py-2">
                      <span
                        className={`px-2 py-1 rounded text-sm ${
                          menu.available
                            ? "bg-green-200 text-green-700"
                            : "bg-red-200 text-red-700"
                        }`}
                      >
                        {menu.available ? "Available" : "Not Available"}
                      </span>
                    </td>
                    <td className="px-4 py-2 flex gap-2">
                      <button
                        onClick={() =>
                          handleAvailabilityToggle(menu.id, menu.available)
                        }
                        className="px-3 py-1 bg-choco text-white rounded hover:bg-coffee transition"
                      >
                        Switch
                      </button>
                      <button
                        onClick={() => handleEdit(menu.id)}
                        className="px-3 py-1 bg-cream text-brown rounded hover:bg-[#e0d4c5] transition"
                      >
                        Edit
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MenuAdmin;
