// src/components/Footer.jsx
export default function Footer({ total, count, disabled, onProceed }) {
  if (count === 0) return null;

  return (
    <footer className="fixed bottom-0 left-0 right-0 bg-[#f5efe6] border-t border-coffee px-4 py-3 flex justify-between items-center">
      <div>
        <p className="font-bold text-coffee">
          Total: Rp{total.toLocaleString()}
        </p>
        <p className="text-sm text-coffee">{count} item</p>
      </div>
      <button
        disabled={disabled}
        onClick={onProceed}
        className={`px-4 py-2 rounded text-white ${
          disabled ? "bg-gray-400" : "bg-coffee hover:bg-opacity-80"
        }`}
      >
        Bayar
      </button>
    </footer>
  );
}
