// src/pages/OrderDetail.jsx
import { useLocation, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

export default function OrderDetail() {
  const location = useLocation();
  const navigate = useNavigate();
  const order = location.state?.order; // Data order dilempar dari "CheckOrder"

  if (!order) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-cream text-coffee">
        <p className="text-lg font-medium">Order tidak ditemukan</p>
        <button
          onClick={() => navigate("/")}
          className="mt-4 px-4 py-2 rounded bg-coffee text-white hover:bg-opacity-80 transition"
        >
          Kembali ke Pemesanan
        </button>
      </div>
    );
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
      className="p-4"
    >
      <div className="min-h-screen bg-cream p-6 text-coffee">
        <h1 className="text-2xl font-bold mb-4">Detail Pemesanan</h1>

        {/* Order ID */}
        <div className="mb-4 p-3 rounded bg-[#f5efe6] border border-coffee">
          <p className="font-semibold">Order ID: {order.id}</p>
        </div>

        {/* Status */}
        <div className="mb-4">
          <span
            className={`px-3 py-1 rounded font-semibold ${
              order.status === "Pending"
                ? "bg-yellow-200 text-yellow-800"
                : "bg-green-200 text-green-800"
            }`}
          >
            {order.status}
          </span>
        </div>

        {/* List menu */}
        <div className="mb-4">
          <h2 className="text-lg font-semibold mb-2">Pesanan:</h2>
          <ul className="space-y-2">
            {order.items.map((item, idx) => (
              <li
                key={idx}
                className="flex justify-between border-b border-gray-300 pb-1"
              >
                <span>
                  {item.qty}x {item.name}
                </span>
                <span>Rp{(item.price * item.qty).toLocaleString()}</span>
              </li>
            ))}
          </ul>
        </div>

        {/* Total harga */}
        <div className="mb-6 font-bold text-lg">
          Total: Rp{order.total.toLocaleString()}
        </div>

        {/* Tombol kembali */}
        <button
          onClick={() => navigate("/")}
          className="bg-[#6F4E37] text-white px-4 py-2 rounded shadow-md hover:shadow-lg hover:bg-[#5c3f2d] active:scale-95 transition"
        >
          Kembali
        </button>
      </div>
    </motion.div>
  );
}
