// src/pages/OrderDetail.jsx
import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { motion } from "framer-motion";
import { getOrderById } from "../services/orderService";

export default function OrderDetail() {
  const { orderId } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const data = await getOrderById(orderId);
        setOrder(data);
      } catch (err) {
        console.error("Gagal ambil detail order:", err);
      }
    };
    fetchOrder();
  }, [orderId]);

  if (!order) return <p className="p-6">Loading...</p>;

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
      className="p-4"
    >
      <div className="min-h-screen bg-cream p-6 text-coffee">
        <h1 className="text-2xl font-bold mb-4">Detail Pemesanan</h1>

        {/* Order ID */}
        <div className="mb-4 p-3 rounded bg-[#f5efe6] border border-coffee">
          <p className="font-semibold">Order ID: {order.id}</p>
          <p className="font-semibold">No Meja: {order.table_number}</p>
        </div>

        {/* Status */}
        <div className="mb-4">
          <span
            className={`px-3 py-1 rounded font-semibold ${
              order.status === "Pending"
                ? "bg-yellow-200 text-yellow-800"
                : "bg-green-200 text-green-800"
            }`}
          >
            {order.status}
          </span>
        </div>

        {/* List menu */}
        <div className="mb-4">
          <h2 className="text-lg font-semibold mb-2">Pesanan:</h2>
          <ul className="space-y-2">
            {order.items.map((item, idx) => (
              <li
                key={idx}
                className="flex justify-between border-b border-gray-300 pb-1"
              >
                <span>
                  {item.qty}x {item.name}
                </span>
                <span>Rp{(item.price * item.qty).toLocaleString()}</span>
              </li>
            ))}
          </ul>
        </div>

        {/* Total harga */}
        <div className="mb-6 font-bold text-lg">
          Total: Rp{order.total_price.toLocaleString()}
        </div>

        {/* Tombol kembali */}
        <button
          onClick={() => navigate("/")}
          className="bg-[#6F4E37] text-white px-4 py-2 rounded shadow-md hover:shadow-lg hover:bg-[#5c3f2d] active:scale-95 transition"
        >
          Kembali
        </button>
      </div>
    </motion.div>
  );
}
